pub fn build_proverb(list: Vec<&str>) -> String {    let mut lines = vec![];    for i in 0..list.len() {        if let Some(second) = list.get(i + 1) {            lines.push(format!(                "For want of a {first} the {second} was lost.",                first = list.get(i).unwrap(),                second = second            ))        }    }    if list.len() > 2 {        lines.push(format!(            "And all for the want of a {third}{second} {first}.",            first = list.get(0).unwrap(),            second = list.get(1).unwrap(),            third = list.get(2).unwrap()        ))    } else if list.len() > 0 {        lines.push(format!(            "And all for the want of a {first}.",            first = list.get(0).unwrap()        ));    }    return lines.join("\n");}